function App() {
  useEffect(() => {
    let unsubscribe: (() => void) | undefined
    let debounceTimeout: NodeJS.Timeout | undefined

    const initializeApp = async () => {
      // Initialize auth store on app startup
      useAuthStore.getState().initialize()

      // Initialize network monitoring
      networkStatus.initialize()

      // Load offline queue from storage before subscribing to network changes
      await useOfflineQueueStore.getState().loadFromStorage()

      // Subscribe to network changes and process queue when online (with debounce)
      unsubscribe = networkStatus.subscribe((status) => {
        if (status.isConnected && (status.isInternetReachable === null || status.isInternetReachable)) {
          // Clear any pending debounce
          if (debounceTimeout) {
            clearTimeout(debounceTimeout)
          }

          // Debounce queue processing to prevent race conditions
          debounceTimeout = setTimeout(() => {
            useOfflineQueueStore.getState().processQueue()
          }, 500)
        }
      })

      // Explicitly trigger queue processing if already online after load
      const currentStatus = networkStatus.getStatus()
      if (
        currentStatus.isConnected &&
        (currentStatus.isInternetReachable === null || currentStatus.isInternetReachable)
      ) {
        useOfflineQueueStore.getState().processQueue()
      }
    }

    initializeApp()

    return () => {
      if (debounceTimeout) {
        clearTimeout(debounceTimeout)
      }
      unsubscribe?.()
      networkStatus.cleanup()
    }
  }, [])

  return (
    <SafeAreaProvider>
      <NavigationContainer />
      <Toast />
      <StatusBar style="light" />
    </SafeAreaProvider>
  )
}

async function globalSetup() {
  console.log('\n[GlobalSetup] Starting E2E test environment...\n')

  // 1. Start backend server (skip if CI already started it)
  if (process.env.E2E_API_BASE_URL) {
    console.log(`[GlobalSetup] Using pre-started backend at ${process.env.E2E_API_BASE_URL}`)
  } else {
    const backend = getBackendManager()
    await backend.start()
    // Store base URL for tests to access
    process.env.E2E_API_BASE_URL = backend.getBaseUrl()
  }

  // 2. Run Detox global setup
  const { default: detoxGlobalSetup } = await import('detox/runners/jest/globalSetup.js')
  await detoxGlobalSetup()

  console.log('\n[GlobalSetup] E2E test environment ready\n')
}

async function globalTeardown() {
  console.log('\n[GlobalTeardown] Cleaning up E2E test environment...\n')

  // 1. Run Detox global teardown first
  const { default: detoxGlobalTeardown } = await import('detox/runners/jest/globalTeardown.js')
  await detoxGlobalTeardown()

  // 2. Stop backend server
  const backend = getBackendManager()
  await backend.stop()
  resetBackendManager()

  console.log('\n[GlobalTeardown] E2E test environment cleaned up\n')
}

